mapper.select:
select a.id histId,a.user_id userId,b.user_name userName,b.true_name trueName,a.start_time startTime,a.exam_id examId,a.exam_paper_id examPaperId,a.enabled,a.point,a.seri_no seriNo,a.content, a.answer_sheet answerSheet,a.duration,a.point_get pointGet,a.submit_time submitTime,a.approved,a.verify_time verifyTime from et_user_exam_history a,et_user b where a.user_id=b.user_id and a.user_id=#{userId} and a.exam_id=#{examId}
and a.approved in
#{item}
order by a.id desc

SELECT a.id questionId,a.content,a.answer,a.analysis,a.question_type_id questionTypeId,a.reference referenceName,a.examing_point examingPoint,b.point_id knowledgePointId,GROUP_CONCAT(d.field_name,' > ',c.point_name,' > ', a.examing_point ,case when a.keyword is null then '' else CONCAT(' 知识关键点：',a.keyword) end) pointName,GROUP_CONCAT(d.field_name) fieldName from et_question a,et_question_2_point b,et_knowledge_point c,et_field d
a.id=b.question_id and b.point_id=c.point_id and c.field_id=d.field_id and a.id in ( select a.question_id from et_question_2_charactorType a where a.charactor_id in (select id from et_charactor_type where xuepaiId=#{xuepaiId}) )
group by a.id,a.content,a.answer,a.analysis,a.question_type_id,a.reference, a.examing_point,b.point_id;

select a.question_id questionId, a.charactor_id charactorId, b.xuepaiId, b.name charactorName, c.name xuepaiName from et_question_2_charactorType a left join et_charactor_type b on a.charactor_id=b.id left join et_charactor_xuepai c on b.xuepaiId=c.id where 1=1
and a.question_id in
#{item}
