mapper.select:
select z.*, f.content parentContent from (SELECT a.id questionId, a.parentId, a.content,a.answer,a.analysis,a.question_type_id questionTypeId,a.reference referenceName,a.examing_point examingPoint,b.point_id knowledgePointId,GROUP_CONCAT(d.field_name,' > ',c.point_name,' > ', case when a.keyword is null then '' else CONCAT(' 知识关键点：',a.keyword) end) pointName,GROUP_CONCAT(d.field_name) fieldName from et_question a,et_question_2_point b,et_knowledge_point c,et_field d
a.id=b.question_id and b.point_id=c.point_id and c.field_id=d.field_id and a.question_type_id=#{typeId} and b.point_id=#{pointId}
group by a.id,a.content,a.answer,a.analysis,a.question_type_id,a.reference, a.examing_point,b.point_id order by a.question_type_id,a.id) z left join et_question_parent f on z.parentId=f.id

select field_id fieldId,field_name fieldName,memo,state, case when exists(select 1 from et_knowledge_point m where m.field_id=a.field_id) then 0 else 1 end removeable from et_field a where a.field_id in ( select fieldId from et_group_2_field where 1=1
and groupId in
#{item}
) order by field_id desc

select z.*, f.content parentContent from (SELECT a.id questionId, a.parentId, a.content,a.answer,a.analysis,a.question_type_id questionTypeId,a.reference referenceName,a.examing_point examingPoint,b.point_id knowledgePointId from et_question a,et_question_2_point b,et_knowledge_point c,et_field d
a.id=b.question_id and b.point_id=c.point_id and c.field_id=d.field_id and d.field_id=#{fieldId}
order by a.question_type_id,a.id) z left join et_question_parent f on z.parentId=f.id

select z.*, f.content parentContent from (select a.id,a.parentId, a.name,a.content,a.question_type_id,a.creator,a.answer,a.analysis,a.reference referenceName,group_concat(concat(d.field_name,'-',c.point_name)) pointName, e.name questionTypeName, examing_point examingPoint, (select group_concat(m.tag_name,' ') from et_tag m,et_question_2_tag n where m.tag_id=n.tag_id and n.question_id=a.id) tags from et_question a,et_question_2_point b,et_knowledge_point c,et_field d,et_question_type e
a.id=b.question_id and b.point_id=c.point_id and c.field_id=d.field_id and a.question_type_id=e.id
and a.id in #{item}
group by a.id,a.name,a.content,a.question_type_id,a.creator,a.answer,a.analysis,a.reference,a.examing_point,e.`name`,a.examing_point,tags) z left join et_question_parent f on z.parentId=f.id order by z.id desc;
